import {useState} from 'react'
import axios from 'axios'

const New = () => {
    const [title, setTitle] = useState('')
    const [genre, setGenre] = useState('')

    const addSong = async (e) => {
        e.preventDefault()
        try {
            let auth = localStorage.getItem('userId')
            let res = await axios.post(`${process.env.REACT_APP_BACKEND_URL}/songs`,

            {
                title: title,
                genre: genre
            },
            {
                headers: {
                    authorization:auth
                }
            })

            console.log(res)
        } catch (error) {
           console.log(error)
        }
    }
    const [song, setSong] = useState({
        title: '',
        genre: ''

    })

    const handleChange = (e) => {
        setSong({
            ...song,

        })
    }




    return (
        <div>
        <>
        <h1>Submit a New Song to the Community!</h1>
        <form onSubmit={addSong}>
            <div>
                <label htmlFor="new-title">Title:</label>
                <input type="text" placeholder="title" value={song.title} onChange={(e) => { setSong({ ...song, title: e.target.value }) }}
                />
            </div>

            <div>
                <label htmlFor="new-genre">Genre:</label>
                <input type="text" placeholder="genre" value={song.genre} onChange={(e) => { setSong({ ...song, genre: e.target.value }) }}
                />
            </div>
                <input type="submit" value="submit" />

        </form>
        </>
        </div>
    )
}

export default New
